mapper.update:
UPDATE sys_user
user_name = #{userName,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, is_deleted = #{isDeleted,jdbcType=INTEGER},
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT COUNT(1)
FROM sys_user
WHERE user_name = #{userName,jdbcType=VARCHAR}
AND is_deleted = 0

    AND id != #{excludeId,jdbcType=BIGINT}
